<h1>Personal Storage Study</h1>

<h3>Thanks for participating!</h3>

<script type="text/javascript">
jQuery(document).ready(function(){
	$('.questions .qcatheader').click(function() {
		$(this).next().toggle('slow');
		return false;
	}).next().show();

   // checkboxes toggle number boxes
   $('#chk_desktops').click(function() {
      if($(this).attr('checked')) {
         $('#num_desktops').show();
      } else {
         $('#num_desktops').hide();
      }
   });
   $('#chk_laptops').click(function() {
      if($(this).attr('checked')) {
         $('#num_laptops').show();
      } else {
         $('#num_laptops').hide();
      }
   });
   $('#chk_smartphone').click(function() {
      if($(this).attr('checked')) {
         $('#details_smartphone').show();
      } else {
         $('#details_smartphone').hide();
      }
   });
   $('#chk_mp3player').click(function() {
      if($(this).attr('checked')) {
         $('#details_mp3player').show();
      } else {
         $('#details_mp3player').hide();
      }
   });

   $('#chk_s_fb').click(function() {
      if($(this).attr('checked')) {
         $('#s_fb_opt').show();
      } else {
         $('#s_fb_opt').hide();
      }
   });

   // hide them initially
   $('#num_desktops').hide();
   $('#num_laptops').hide();
   $('#details_smartphone').hide();
   $('#details_mp3player').hide();
   // hide optional bits by default
   //$('.qoptional').hide();
   $('#s_fb_opt').hide();

   // slider setup
	$(function() {
		$(".trustslider").slider({
			value:3,
			min: 0,
			max: 6,
			slide: function(event, ui) {
				tval = parseInt(ui.value);
            switch (tval) {
               case 0:
                  $(this).next().val("None at all");
                  break;
               case 1:
                  $(this).next().val("Very little");
                  break;
               case 2:
                  $(this).next().val("Little");
                  break;
               case 3:
                  $(this).next().val("No indication");
                  break;
               case 4:
                  $(this).next().val("Some");
                  break;
               case 5:
                  $(this).next().val("Reasonably much");
                  break;
               case 6:
                  $(this).next().val("Very much");
                  break;
               default:
                  $(this).next().val("Unknown?! (" + ui.value + ")");
            }
			}
		});
      // slider init (?)
		$("#s_fb_trustlbl").val("No indication");
	});

});
</script>

