<script type="text/javascript">
jQuery(document).ready(function(){

   $("#questionform").validationEngine()

   //$.validationEngine.loadValidation("#date")
   //alert($("#formID").validationEngine({returnIsValid:true}))
   //$.validationEngine.buildPrompt("#date","This is an example","error")	 		 // Exterior prompt build example
   //$.validationEngine.closePrompt(".formError",true) 							// CLOSE ALL OPEN PROMPTS


   function toggleOptional(checkbox, target) {
      if($(checkbox).attr('checked')) {
         $(target).show();
      } else {
         $(target).hide();
      }
   }

	$('.questions .qcatheader').click(function() {
		$(this).next().toggle('slow');
		return false;
	}).next().show();

   // checkboxes toggle number boxes
   $('#chk_desktops').click(function() {
      toggleOptional(this, '#num_desktops');
   });
   $('#chk_laptops').click(function() {
      toggleOptional(this, '#num_laptops');
   });
   $('#chk_smartphone').click(function() {
      toggleOptional(this, '#details_smartphone');
   });
   $('#chk_mp3player').click(function() {
      toggleOptional(this, '#details_mp3player');
   });
   $('#chk_usbstick').click(function() {
      toggleOptional(this, '#num_usbsticks');
   });

   $('#chk_s_fb').click(function() {
      toggleOptional(this, '#s_fb_opt');
   });
   $('#chk_s_google').click(function() {
      toggleOptional(this, '#s_google_opt');
   });
   $('#chk_s_bing').click(function() {
      toggleOptional(this, '#s_bing_opt');
   });
   $('#chk_s_yahoo').click(function() {
      toggleOptional(this, '#s_yahoo_opt');
   });
   $('#chk_s_twitter').click(function() {
      toggleOptional(this, '#s_twitter_opt');
   });
   $('#chk_s_dropbox').click(function() {
      toggleOptional(this, '#s_dropbox_opt');
   });
   $('#chk_s_others').click(function() {
      toggleOptional(this, '#s_others_opt');
   });

   // hide them initially
   $('#num_desktops').hide();
   $('#num_laptops').hide();
   $('#num_usbsticks').hide();
   $('#details_smartphone').hide();
   $('#details_mp3player').hide();
   // hide optional bits by default
   //$('.qoptional').hide();
   $('#s_fb_opt').hide();
   $('#s_google_opt').hide();
   $('#s_bing_opt').hide();
   $('#s_yahoo_opt').hide();
   $('#s_twitter_opt').hide();
   $('#s_dropbox_opt').hide();
   $('#s_others_opt').hide();

   // slider setup
	$(function() {
		$(".trustslider").slider({
			value:3,
			min: 0,
			max: 6,
			slide: function(event, ui) {
				tval = parseInt(ui.value);
            switch (tval) {
               case 0:
                  $(this).next().val("None at all");
                  break;
               case 1:
                  $(this).next().val("Very little");
                  break;
               case 2:
                  $(this).next().val("Little");
                  break;
               case 3:
                  $(this).next().val("No indication");
                  break;
               case 4:
                  $(this).next().val("Some");
                  break;
               case 5:
                  $(this).next().val("Reasonably much");
                  break;
               case 6:
                  $(this).next().val("Very much");
                  break;
               default:
                  $(this).next().val("Unknown?! (" + ui.value + ")");
            }
			}
		});
      // slider value init
		$("#s_fb_trustlbl").val("No indication");
		$("#s_google_trustlbl").val("No indication");
		$("#s_bing_trustlbl").val("No indication");
		$("#s_yahoo_trustlbl").val("No indication");
		$("#s_twitter_trustlbl").val("No indication");
		$("#s_dropbox_trustlbl").val("No indication");
	});

});

// form submission
function submitAndStop() {
   document.questionform.action = 'survey2a.php';
}

function submitAndNext() {
